        if (!seen.has(id)){
          out.bundle.modules[id] = deepClone(n.modules[id]);
          seen.add(id);
        } else {
          shadows.push({ id, from: n.name, note: "duplicate kept first" });
        }
      }
    }
    out.bundle.modules['meta/merge_shadows.json'] = { language: 'json', code: JSON.stringify({shadows}, null, 2) };
    return out;
  }
