(function(){
  'use strict';
  function deepClone(o){ return JSON.parse(JSON.stringify(o)); }
  function mergeBundles(parts){
    const out = { bundle: { name: "noa-merged", version: "Px", entry: "kernel/bootstrap", modules: {} } };
    for(const p of parts){
      const b = p && p.bundle;
      if(!b) continue;
      const mods = b.modules || {};
      for(const id of Object.keys(mods)){
        if(!out.bundle.modules[id]){
          out.bundle.modules[id] = deepClone(mods[id]);
        } else {
          // keep first occurrence; record shadow info
          const shKey = "meta/shadows.json";
          const rec = (out.bundle.modules[shKey] && JSON.parse(out.bundle.modules[shKey].code)) || { shadows: [] };
          rec.shadows.push({ id, note: "duplicate encountered" });
          out.bundle.modules[shKey] = { language: "json", code: JSON.stringify(rec, null, 2) };
        }
      }
    }
    out.bundle.modules["integrity/merged_index.json"] = { language: "json", code: JSON.stringify({ unique: Object.keys(out.bundle.modules).length }, null, 2) };
    return out;
  }
  if (typeof module !== 'undefined') module.exports = { mergeBundles };
  return { mergeBundles };
})();

